const data = [];
for (const item of $input.all()) {
  data.push(item.json);
}
return [
  {
    content: JSON.stringify(data),
  },
];

parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "QuickChart Configuration",
  "type": "object",
  "required": ["type", "data"],
  "properties": {
    "type": {
      "type": "string",
      "description": "Chart type, e.g., bar, line, pie, radar, etc.",
      "enum": ["bar", "line", "pie", "doughnut", "radar", "polarArea"]
    },
    "data": {
      "type": "object",
      "required": ["labels", "datasets"],
      "properties": {
        "labels": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Labels for the X-axis or segments"
        },
        "datasets": {
          "type": "array",
          "minItems": 1,
          "items": {
            "type": "object",
            "required": ["label", "data"],
            "properties": {
              "label": {
                "type": "string",
                "description": "Label for the dataset"
              },
              "data": {
                "type": "array",
                "items": {
                  "type": "number"
                },
                "description": "Data points"
              },
              "backgroundColor": {
                "type": ["string", "array"],
                "description": "Background color(s)"
              },
              "borderColor": {
                "type": ["string", "array"],
                "description": "Border color(s)"
              },
              "fill": {
                "type": ["boolean", "string"],
                "description": "Whether to fill under the line (for line chart)"
              },
              "tension": {
                "type": "number",
                "description": "Line tension (for line chart, 0 = straight lines)"
              }
            }
          }
        }
      }
    },
    "options": {
      "type": "object",
      "description": "Chart display options",
      "properties": {
        "title": {
          "type": "object",
          "properties": {
            "display": { "type": "boolean" },
            "text": { "type": "string" }
          }
        },
        "scales": {
          "type": "object",
          "properties": {
            "x": {
              "type": "object",
              "properties": {
                "title": {
                  "type": "object",
                  "properties": {
                    "display": { "type": "boolean" },
                    "text": { "type": "string" }
                  }
                }
              }
            },
            "y": {
              "type": "object",
              "properties": {
                "title": {
                  "type": "object",
                  "properties": {
                    "display": { "type": "boolean" },
                    "text": { "type": "string" }
                  }
                },
                "beginAtZero": { "type": "boolean" }
              }
            }
          }
        }
      }
    }
  }
}
